<?php

namespace app\modules\filemanage\controllers;

use Yii;
class FileController extends \yii\web\Controller
{
	public function init()
	{
		parent::init();
		$this->initImage();
	}

	private $_files;
	public function initImage(){
		for ($strlen=0; $strlen < 10; $strlen++) { 
			$this->_files[$strlen] = '<div class="col-md-3 col-xs-6"> <a href="#" class="thumbnail"> <img alt="100%x180" data-src="holder.js/100%x180" src="data:image/svg+xml;base64," data-holder-rendered="true" style="height: 180px; width: 100%; display: block;"> </a> </div>';
		}
	}

    public function actionIndex()
    {
        return $this->render('index',[
        	'files' => $this->_files
        ]);
    }
}
